async function onRequestPort() {
  // Device ID                : USB\VID_0694&PID_0009\205F31754252
  const filters = [{ usbVendorId: 0x1684, usbProductId: 0x0009 }]; // ???

  port.value = await nav.serial.requestPort(filters);
