function toHex(data: Uint8Array, startIndex: number, length: number) {
  var hexString = '';
  for (let i = startIndex; i < startIndex + length; i++)
    hexString += '\\x' + data[i].toString(16);

  console.log('to hex', hexString);
  return hexString;
}

